                      ) : (
                        <></>
                      )}

                      <div className="explanation-text">{explanation}</div>
                    </div>
                  ) : (
                    <div
                      className="explanation-div"
                      style={{ display: showExplanation ? "" : "none" }}
                    >
                      <div className="explanation-text">
